PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "first_name" varchar not null, "last_name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "address" varchar not null, "street" varchar not null, "city" varchar not null, "province" varchar not null, "country" varchar not null, "postal_code" varchar not null, "phone_number" varchar not null, "password" varchar not null, "remember_token" varchar, "is_admin" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "password_resets" ("email" varchar not null, "token" varchar not null, "created_at" datetime);
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "personal_access_tokens" ("id" integer primary key autoincrement not null, "tokenable_type" varchar not null, "tokenable_id" integer not null, "name" varchar not null, "token" varchar not null, "abilities" text, "last_used_at" datetime, "expires_at" datetime, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "products" ("id" integer primary key autoincrement not null, "name" varchar not null, "image" varchar not null, "summary" text not null, "color" varchar not null, "material" varchar not null, "unit_price" numeric not null, "description" text not null, "brand" varchar not null, "weight" varchar not null, "dimensions" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "taxes" ("id" integer primary key autoincrement not null, "province" varchar not null, "pst" numeric not null default '0', "gst" numeric not null default '0', "hst" numeric not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "orders" ("id" integer primary key autoincrement not null, "order_date" date not null, "sub_total" numeric not null, "pst" numeric, "gst" numeric, "hst" numeric, "total" numeric not null, "address" varchar not null, "city" varchar not null, "province" varchar not null, "postal_code" varchar not null, "user_id" integer not null, "status" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "line_items" ("id" integer primary key autoincrement not null, "order_id" integer not null, "product_id" integer not null, "name" varchar not null, "quantity" integer not null, "unit_price" numeric not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "transactions" ("id" integer primary key autoincrement not null, "order_id" integer not null, "status" tinyint(1) not null default '0', "transaction" text not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "reviews" ("id" integer primary key autoincrement not null, "product_id" integer not null, "user_id" integer not null, "comment" text not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "messages" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "message" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "category_product" ("category_id" integer not null, "product_id" integer not null);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "password_resets_email_index" on "password_resets" ("email");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE INDEX "personal_access_tokens_tokenable_type_tokenable_id_index" on "personal_access_tokens" ("tokenable_type", "tokenable_id");
CREATE UNIQUE INDEX "personal_access_tokens_token_unique" on "personal_access_tokens" ("token");
COMMIT;
